definition.script:
pipeline {
    agent any

    stages {

        stage('Initial Dictionary Load') {
            steps {
                build job: 'Dictionary ETL - Load Dictionary from HPDS API', parameters: [
                                        booleanParam(name: 'CLEAR_DATABASE', value: true)
                                    ]
            }
        }
        stage('Establishing search vectors') {
            steps {
                build job: 'Run Dictionary Weights'
            }
        }
    }
}
