definition.script:
node {   
    stage('Auth Schema Migration') {  
        git url: 'https://github.com/hms-dbmi/pic-sure-auth-microapp'
        docker.image('dbmi/pic-sure-db-migrations:pic-sure-db-migration_v1.0').inside('--network=picsure -v /usr/local/docker-config/flyway/auth:/opt/flyway-migrations/auth "--entrypoint=" ') { 
            sh "rm -rf /opt/flyway-migrations/auth/sql"
            sh "cp -R ./pic-sure-auth-db/db/sql /opt/flyway-migrations/auth/sql"
            sh "/opt/flyway/flyway -X -configFiles=/opt/flyway-migrations/auth/flyway-auth.conf migrate"
            sleep(time:10,unit:"SECONDS") 
        }
    }
    stage('Picsure Schema Migration') {    
        git url: 'https://github.com/hms-dbmi/pic-sure'
        docker.image('dbmi/pic-sure-db-migrations:pic-sure-db-migration_v1.0').inside('--network=picsure -v /usr/local/docker-config/flyway/picsure:/opt/flyway-migrations/picsure "--entrypoint=" ') { 
            sh "rm -rf /opt/flyway-migrations/picsure/sql"
            sh "cp -R ./pic-sure-api-data/src/main/resources/db/sql /opt/flyway-migrations/picsure/sql"
            sh "/opt/flyway/flyway -X -configFiles=/opt/flyway-migrations/picsure/flyway-picsure.conf migrate"
            sleep(time:10,unit:"SECONDS") 
        }
    }
    stage('Project Specific Migrations') { 
        git url: env.project_specific_override_repo
        sh """
             sed -i "s/__APPLICATION_UUID__/`cat /usr/local/docker-config/APP_ID_HEX`/g" ./custom-migrations/auth/V2__CONFIGURE_PIC_SURE_APPLICATION.sql
            sed -i "s/__RESOURCE_UUID__/`cat /usr/local/docker-config/RESOURCE_ID_HEX`/g" ./custom-migrations/picsure/V2__CREATE_HPDS_RESOURCE.sql
        """
        docker.image('dbmi/pic-sure-db-migrations:pic-sure-db-migration_v1.0').inside('--network=picsure -v /usr/local/docker-config/flyway/picsure:/opt/flyway-migrations/picsure "--entrypoint=" ') {
            sh "rm -rf /opt/flyway-migrations/picsure/sql"
            sh "cp -R ./custom-migrations/picsure /opt/flyway-migrations/picsure/sql"
            sh "/opt/flyway/flyway -X -baselineOnMigrate=true -configFiles=/opt/flyway-migrations/picsure/flyway-picsure.conf -table=flyway_custom_schema_history migrate"
            sleep(time:10,unit:"SECONDS") 
        }
        docker.image('dbmi/pic-sure-db-migrations:pic-sure-db-migration_v1.0').inside('--network=picsure -v /usr/local/docker-config/flyway/auth:/opt/flyway-migrations/auth "--entrypoint=" ') {
            sh "rm -rf /opt/flyway-migrations/auth/sql"
            sh "cp -R ./custom-migrations/auth /opt/flyway-migrations/auth/sql"
            sh "/opt/flyway/flyway -X -baselineOnMigrate=true -configFiles=/opt/flyway-migrations/auth/flyway-auth.conf -table=flyway_custom_schema_history migrate"
            sleep(time:10,unit:"SECONDS") 
        }
    }
    stage('Clean Workspace'){ 
        cleanWs() 
    }

}
