definition.script:
import groovy.json.JsonSlurper;

def retrieveBuildSpecId;
def pipelineBuildId;
def build_hashes = {};

def resourceURL = "https://${SERVERNAME}/picsure/"

pipeline {
    agent any 
    stages {
        stage('Retrieve Build Spec') { 
            steps {
                script {
                    def result = build job: 'Retrieve Build Spec'
                    retrieveBuildSpecId = result.number
                }
                script {
                    copyArtifacts filter: '*', projectName: 'Retrieve Build Spec', selector: specific(""+retrieveBuildSpecId)
                    sh 'cat build-spec.json'
                    sh 'cat pipeline_git_commit.txt'
                    sh 'pwd'
                    def buildSpec = new JsonSlurper().parse(new File('/var/jenkins_home/workspace/Passthrough Resource Pipeline/build-spec.json'))
                    pipelineBuildId = new File('/var/jenkins_home/workspace/Passthrough Resource Pipeline/pipeline_git_commit.txt').text.trim()
                    for(def build : buildSpec.application){
                        build_hashes[build.project_job_git_key] = build.git_hash
                    }
                }
            }
        }
        stage('Create Resource') { 
            steps {
                script {
                    def result = build job: 'Create PIC-SURE PassThrough Resource', parameters: [[$class: 'StringParameterValue', name: 'RESOURCE_NAME', value: $RESOURCE_NAME],[$class: 'StringParameterValue', name: 'RESOURCE_DESC', value: $RESOURCE_DESC],[$class: 'StringParameterValue', name: 'RESOURCE_PATH', value: $RESOURCE_PATH],[$class: 'StringParameterValue', name: 'TARGET_PICSURE_URL', value: $resourceURL],[$class: 'StringParameterValue', name: 'TARGET_RESOURCE_ID', value: $TARGET_RESOURCE_ID],[$class: 'StringParameterValue', name: 'TARGET_RESOURCE_TOKEN', value: $TARGET_RESOURCE_TOKEN],[$class: 'StringParameterValue', name: 'git_hash', value: build_hashes['PSA']]]
                }
            }
        }
        stage('Update Truststore'){
            steps {
               script {
                   def result = build job: 'Import Server Cert to TrustStore', parameters: [[$class: 'StringParameterValue', name: 'SERVERNAME', value: pipelineBuildId], [$class: 'StringParameterValue', name: 'CERTALIAS', value: $RESOURCE_NAME]]
               }
            }
        }
    }
}
